import React, { useState } from 'react';
import './App.css';

function App() {
  const [userInput, setUserInput] = useState('');
  const [chat, setChat] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const sendMessage = async () => {
    if (!userInput.trim()) return;

    setIsLoading(true);

    try {
      const response = await fetch("http://localhost:5198/api/Chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ userMessage: userInput })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.details || "API request failed");
      }

      const data = await response.json();
      setChat(prev => [...prev, data]);
      setUserInput('');
    } catch (error) {
      console.error("API Error:", error);
      alert(`Error: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>Chat with Samsoma ðŸ¤–</h1>
      <div className="chat-box">
        {chat.map((c, index) => (
          <div key={index} className="message">
            <p><strong>You:</strong> {c.userMessage}</p>
            <p><strong>Bot:</strong> {c.botResponse}</p>
          </div>
        ))}
        {isLoading && <p>Loading...</p>}
      </div>
      <div className="input-area">
        <input
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          placeholder="Type your message..."
          disabled={isLoading}
          onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
        />
        <button onClick={sendMessage} disabled={isLoading || !userInput.trim()}>
          {isLoading ? 'Sending...' : 'Send'}
        </button>
      </div>
    </div>
  );
}

export default App;
